package chap01_print;

public class Print01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 한줄 주석 : 실행X(컴파일 되지 않음), 메모장 같은 느낌
		//ctrl + / : 한줄 주석 단축키
		/* ~~~  여러줄 주석 */
		/* (블럭을 잡고)ctrl + shift + /: 여러 줄 주석
		 * --> 그냥 둬도 되고, 지워도 됨(별)
		*/
		
		//print : 다음 줄로 안넘어가고 옆에 바로 프린트됨
		//println : 다음 줄로 넘어감
		System.out.println("본인 이름: 한수연");  //세미콜론 필수
		System.out.println("더조은 아카데미 학원");
		
	}

}
